pragma solidity ^0.8.13;

import "./InvoiceFinanceInterestTermsContract.sol";
import "./InvoiceDebtRegistry.sol";
import "../../../storage/Registry.sol";
import '@openzeppelin/contracts/interfaces/IERC20.sol';

/**
* The RepaymentRouter routes allowers payers to make repayments on any
* given debt agreement in any given token by routing the payments to
* the debt agreement's beneficiary.  Additionally, the router acts
* as a trusted oracle to the debt agreement's terms contract, informing
* it of exactly what payments have been made in what quantity and in what token.
*/
contract InvoiceLoanRepaymentRouter is PausableUpgradeable, OwnableUpgradeable {

    using ConfigHelper for Registry;
    Registry public registry;

    enum Errors {
        DEBT_AGREEMENT_NONEXISTENT,
        PAYER_BALANCE_OR_ALLOWANCE_INSUFFICIENT,
        REPAYMENT_REJECTED_BY_TERMS_CONTRACT
    }

    event LogRepayment(
        bytes32 indexed _agreementId,
        address indexed _payer,
        address indexed _beneficiary,
        uint _amount,
        address _token
    );

    event LogError(uint8 indexed _errorId, bytes32 indexed _agreementId);

    function initialize(Registry _registry) public initializer {
        __Pausable_init_unchained();
        __Ownable_init_unchained();
        registry = _registry;
    }

    // Validate repayment request parametters
    function _assertRepaymentRequest(bytes32 _agreementId, address _payer, uint256 _amount, address _tokenAddress)
        internal view
    {
        require(_tokenAddress != address(0), "Token address must different with NULL.");
        require(_amount > 0, "Amount must greater than 0.");

        // Ensure agreement exists.
        require(
            registry.getInvoiceDebtRegistry().doesEntryExist(_agreementId),
            "Inventory Debt Registry: Agreement Id does not exists."
        );
    }


    function _doRepay(bytes32 _agreementId, address _payer, uint256 _amount, address _tokenAddress)
        internal
    {
        // Notify terms contract
        InvoiceDebtRegistry invoiceDebtRegistry = registry.getInvoiceDebtRegistry();
        address termsContract = invoiceDebtRegistry.getTermsContract(_agreementId);
        address beneficiary = invoiceDebtRegistry.getBeneficiary(_agreementId);
        address debtor = invoiceDebtRegistry.getDebtor(_agreementId);

        uint remains = InvoiceFinanceInterestTermsContract(termsContract).registerRepayment(
            _agreementId,
            _payer,
            beneficiary,
            _amount,
            _tokenAddress
        );

        // Transfer amount to creditor
        require(
            IERC20(_tokenAddress).transferFrom(_payer, beneficiary, _amount - remains),
            "Unsuccessfully transferred repayment amount to Creditor.");

        // Transfer remain amount to debtor
        if (debtor != _payer && remains > 0) {
            require(
                IERC20(_tokenAddress).transferFrom(_payer, debtor, remains),
                "Unsuccessfully transferred remain repayment amount to Debtor."
            );
        }

        // Log event for repayment
        emit LogRepayment(_agreementId, _payer, beneficiary, _amount, _tokenAddress);
    }


    /**
     */
    function repayFromInvoicePayment(
        bytes32 agreementId,
        address payer,
        uint256 amount,
        address tokenAddress
    )
        public
        whenNotPaused
        returns (uint)
    {
        _assertRepaymentRequest(agreementId, payer, amount, tokenAddress);
        _doRepay(agreementId, payer, amount, tokenAddress);
        return amount;
    }

    // Mannual repay by using Fiat tokens
    function repay(bytes32 agreementId, address payer, uint256 amount, address tokenAddress)
        public
        whenNotPaused
        returns (uint)
    {
        _assertRepaymentRequest(agreementId, payer, amount, tokenAddress);
        _doRepay(agreementId, payer, amount, tokenAddress);
        return amount;
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }
}
","keccak256":"0x445aeae1cacf52c6c094e7373487ec8cf47c5a69e5eaa84e4adb343ac750e1b2","license":"MIT"},"contracts/protocol/note-sale/MintedIncreasingInterestTGE.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './crowdsale/IncreasingInterestCrowdsale.sol';
import './base/LongSaleInterest.sol';

contract MintedIncreasingInterestTGE is IncreasingInterestCrowdsale, LongSaleInterest {
    using ConfigHelper for Registry;

    bool public longSale;
    uint256 public timeStartEarningInterest;
    uint256 public termLengthInSeconds;
    uint256 public interestRate;
    uint256 public yield;

    function initialize(
        Registry _registry,
        address _pool,
        address _token,
        address _currency,
        bool _longSale
    ) public initializer {
        __Crowdsale__init(_registry, _pool, _token, _currency);

        longSale = _longSale;
    }

    function isLongSale() public view override returns (bool) {
        return longSale;
    }

    function setYield(uint256 _yield) public whenNotPaused onlyRole(OWNER_ROLE) {
        yield = _yield;
    }

    function setupLongSale(
        uint256 _interestRate,
        uint256 _termLengthInSeconds,
        uint256 _timeStartEarningInterest
    ) public whenNotPaused nonReentrant securitizationPoolRestricted {
        if (isLongSale()) {
            interestRate = _interestRate;
            timeStartEarningInterest = _timeStartEarningInterest;
            termLengthInSeconds = _termLengthInSeconds;
            yield = _interestRate;
        }
    }

    function getLongSaleTokenPrice(uint256 timestamp) public view returns (uint256) {
        if (!finalized) return (RATE_SCALING_FACTOR**2) / rate;
        else if (
            Configuration.NOTE_TOKEN_TYPE(INoteToken(token).noteTokenType()) == Configuration.NOTE_TOKEN_TYPE.JUNIOR
        ) {
            address sotTgeAddress = ISecuritizationPool(pool).tgeAddress();
            if (sotTgeAddress != address(0) && !FinalizableCrowdsale(sotTgeAddress).finalized())
                return (RATE_SCALING_FACTOR**2) / rate;
            return registry.getDistributionAssessor().calcTokenPrice(pool, token);
        } else {
            require(
                timeStartEarningInterest != 0,
                'MintedIncreasingInterestTGE: timeStartEarningInterest need to be setup'
            );
            return getPurchasePrice(interestRate, yield, timestamp - timeStartEarningInterest, termLengthInSeconds);
        }
    }

    function getLongSaleTokenAmount(uint256 currencyAmount) public view override returns (uint256) {
        return
            _getTokenAmount((currencyAmount * PURCHASE_PRICE_SCALING_FACTOR) / getLongSaleTokenPrice(block.timestamp));
    }

    function startNewRoundSale(
        uint256 openingTime,
        uint256 closingTime,
        uint256 rate,
        uint256 cap
    ) external whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        _preValidateNewSaleRound();

        // call inner function for each extension
        _newSaleRound(rate);
        newSaleRoundTime(openingTime, closingTime);
        _setTotalCap(cap);
    }

    function _preValidateNewSaleRound() internal view {
        require(hasClosed() || totalCapReached(), 'MintedIncreasingInterestTGE: Previous round not closed');
        require(timeInterval > 0, 'MintedIncreasingInterestTGE: Time interval increasing interest is 0');
    }
}
","keccak256":"0x6974c786ed01bd0c8502538e697f99f71df8cbe6d4fb49462ed68f32816ae185","license":"MIT"},"contracts/protocol/note-sale/MintedNormalTGE.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './base/LongSaleInterest.sol';
import "../../libraries/ConfigHelper.sol";
import './crowdsale/FinalizableCrowdsale.sol';

contract MintedNormalTGE is FinalizableCrowdsale, LongSaleInterest {
    using ConfigHelper for Registry;

    bool public longSale;
    uint256 public timeStartEarningInterest;
    uint256 public termLengthInSeconds;
    uint256 public interestRate;
    uint256 public yield;

    function initialize(
        Registry _registry,
        address _pool,
        address _token,
        address _currency,
        bool _longSale
    ) public initializer {
        __Crowdsale__init(_registry, _pool, _token, _currency);

        longSale = _longSale;
    }

    function isLongSale() public view override returns (bool) {
        return longSale;
    }

    function setYield(uint256 _yield) public whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        yield = _yield;
    }

    function setupLongSale(
        uint256 _interestRate,
        uint256 _termLengthInSeconds,
        uint256 _timeStartEarningInterest
    ) public whenNotPaused nonReentrant securitizationPoolRestricted {
        if (isLongSale()) {
            interestRate = _interestRate;
            timeStartEarningInterest = _timeStartEarningInterest;
            termLengthInSeconds = _termLengthInSeconds;
            yield = _interestRate;
        }
    }

    function getLongSaleTokenPrice(uint256 timestamp) public view returns (uint256) {
        if (!finalized) return (RATE_SCALING_FACTOR**2) / rate;
        else if (
            Configuration.NOTE_TOKEN_TYPE(INoteToken(token).noteTokenType()) == Configuration.NOTE_TOKEN_TYPE.JUNIOR
        ) {
            address sotTgeAddress = ISecuritizationPool(pool).tgeAddress();
            if (sotTgeAddress != address(0) && !FinalizableCrowdsale(sotTgeAddress).finalized())
                return (RATE_SCALING_FACTOR**2) / rate;
            return registry.getDistributionAssessor().calcTokenPrice(pool, token);
        } else {
            require(
                timeStartEarningInterest != 0,
                'MintedIncreasingInterestTGE: timeStartEarningInterest need to be setup'
            );
            return getPurchasePrice(interestRate, yield, timestamp - timeStartEarningInterest, termLengthInSeconds);
        }
    }

    function getLongSaleTokenAmount(uint256 currencyAmount) public view override returns (uint256) {
        return
            _getTokenAmount((currencyAmount * PURCHASE_PRICE_SCALING_FACTOR) / getLongSaleTokenPrice(block.timestamp));
    }

    function startNewRoundSale(
        uint256 openingTime,
        uint256 closingTime,
        uint256 rate,
        uint256 cap
    ) external whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        _preValidateNewSaleRound();

        // call inner function for each extension
        _newSaleRound(rate);
        newSaleRoundTime(openingTime, closingTime);
        _setTotalCap(cap);
    }

    function _preValidateNewSaleRound() internal view {
        require(hasClosed() || totalCapReached(), 'MintedIncreasingInterestTGE: Previous round not closed');
    }
}
","keccak256":"0x9c2f42653c4f7ca19a1a63716889aedc70f76558cbf9c2b4766bd8ea51e532a5","license":"MIT"},"contracts/protocol/note-sale/base/LongSaleInterest.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '../../../libraries/UntangledMath.sol';

contract LongSaleInterest {
    uint256 public constant YEAR_LENGTH_IN_DAYS = 365;
    // All time units in seconds
    uint256 public constant MINUTE_LENGTH_IN_SECONDS = 60;
    uint256 public constant HOUR_LENGTH_IN_SECONDS = MINUTE_LENGTH_IN_SECONDS * 60;
    uint256 public constant DAY_LENGTH_IN_SECONDS = HOUR_LENGTH_IN_SECONDS * 24;
    uint256 public constant YEAR_LENGTH_IN_SECONDS = DAY_LENGTH_IN_SECONDS * YEAR_LENGTH_IN_DAYS;

    // To convert an encoded interest rate into its equivalent in percents,
    // divide it by INTEREST_RATE_SCALING_FACTOR_PERCENT -- e.g.
    //     10,000 => 1% interest rate
    uint256 public constant INTEREST_RATE_SCALING_FACTOR_PERCENT = 10**4;
    uint256 public constant PURCHASE_PRICE_SCALING_FACTOR = 10**4;

    function getPurchasePrice(
        uint256 _interestRate,
        uint256 _yield,
        uint256 _durationLengthInSec,
        uint256 _termLengthInSeconds
    ) public pure returns (uint256) {
        require(_termLengthInSeconds > _durationLengthInSec, "LongSaleInterest: _termLength must be greater than durationLength");
        uint256 moreDecimal = UntangledMath.ONE / INTEREST_RATE_SCALING_FACTOR_PERCENT / 100;
        _interestRate = _interestRate * moreDecimal;
        _yield = _yield * moreDecimal;

        uint256 pricipalWithInterestInPercent = UntangledMath.ONE + _interestRate / YEAR_LENGTH_IN_SECONDS;

        uint256 pricipalWithYieldInPercent = UntangledMath.ONE + _yield / YEAR_LENGTH_IN_SECONDS;

        uint256 durationToEndTerm = _termLengthInSeconds - _durationLengthInSec;
        return
            (UntangledMath.rpow(pricipalWithInterestInPercent, _termLengthInSeconds, UntangledMath.ONE) *
                PURCHASE_PRICE_SCALING_FACTOR) /
            UntangledMath.rpow(pricipalWithYieldInPercent, durationToEndTerm, UntangledMath.ONE);
    }
}
","keccak256":"0xbfcdefb1f7bb53f131c1f262f31bd8e6c1de2473583cfbe535af68e0fe80ffc6","license":"MIT"},"contracts/protocol/note-sale/crowdsale/Crowdsale.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '../../../base/UntangledBase.sol';
import '../../../interfaces/ISecuritizationPool.sol';
import '../../../libraries/ConfigHelper.sol';
import '../../../interfaces/INoteToken.sol';
import '@openzeppelin/contracts/token/ERC20/ERC20.sol';

abstract contract Crowdsale is UntangledBase {
    using ConfigHelper for Registry;

    Registry public registry;

    // decimal calculating for rate
    uint256 public constant RATE_SCALING_FACTOR = 10**4;

    address public pool;

    // The token being sold
    address public token;
    // The token for pay
    address public currency;

    // How many token units a buyer gets per currency.
    uint256 public rate; // support by RATE_SCALING_FACTOR decimal numbers
    bool public hasStarted;

    // Amount of currency raised
    uint256 public currencyRaised;
    uint256 public tokenRaised;

    uint256 public totalCap;

    mapping(address => uint256) public currencyRaisedByInvestor;

    event TokensPurchased(address indexed purchaser, address indexed beneficiary, uint256 value, uint256 amount);

    function __Crowdsale__init(
        Registry _registry,
        address _pool,
        address _token,
        address _currency
    ) internal onlyInitializing {
        __UntangledBase__init_unchained(_msgSender());
        registry = _registry;
        pool = _pool;
        token = _token;
        currency = _currency;
    }

    modifier securitizationPoolRestricted() {
        require(_msgSender() == pool, 'Crowdsale: Caller must be pool');
        _;
    }

    modifier smpRestricted() {
        require(_msgSender() == address(registry.getSecuritizationManager()), 'Crowdsale: Caller must be pool');
        _;
    }

    function addFunding(uint256 additionalCap) public whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        require(additionalCap > 0, 'Crowdsale: total cap is 0');

        totalCap = additionalCap + totalCap;
    }

    function _newSaleRound(uint256 newRate) internal {
        require(!hasStarted, 'Crowdsale: Sale round overflow');

        hasStarted = true;
        rate = newRate;
    }

    function buyTokens(
        address payee,
        address beneficiary,
        uint256 currencyAmount
    ) external whenNotPaused nonReentrant smpRestricted returns (uint256) {
        uint256 tokenAmount = isLongSale() ? getLongSaleTokenAmount(currencyAmount) : _getTokenAmount(currencyAmount);

        _preValidatePurchase(beneficiary, currencyAmount, tokenAmount);

        // update state
        currencyRaised += currencyAmount;
        currencyRaisedByInvestor[beneficiary] += currencyAmount;

        tokenRaised += tokenAmount;

        _claimPayment(payee, currencyAmount);
        _processPurchase(beneficiary, tokenAmount);
        emit TokensPurchased(_msgSender(), beneficiary, currencyAmount, tokenAmount);

        _forwardFunds(ISecuritizationPool(pool).pot(), currencyAmount);

        return tokenAmount;
    }

    function isDistributedFully() public view returns (bool) {
        return currencyRaised == totalCap;
    }

    function getTokenRemainAmount() public view returns (uint256) {
        return IERC20(token).balanceOf(address(this));
    }

    function getCurrencyRemainAmount() public view virtual returns (uint256) {
        return totalCap - currencyRaised;
    }

    function isLongSale() public view virtual returns (bool);

    function getLongSaleTokenAmount(uint256 currencyAmount) public view virtual returns (uint256);

    function _defaultPreValidatePurchase(
        address beneficiary,
        uint256 currencyAmount,
        uint256 tokenAmount
    ) internal view {
        require(beneficiary != address(0), 'Crowdsale: beneficiary is zero address');
        //        require(currencyAmount != 0, "currency amount is 0");
        require(tokenAmount != 0, 'Crowdsale: token amount is 0');
        require(isUnderTotalCap(currencyAmount), 'Crowdsale: cap exceeded');
    }

    function _preValidatePurchase(
        address beneficiary,
        uint256 currencyAmount,
        uint256 tokenAmount
    ) internal view virtual {
        _defaultPreValidatePurchase(beneficiary, currencyAmount, tokenAmount);
    }

    function _deliverTokens(address beneficiary, uint256 tokenAmount) internal {
        INoteToken(token).mint(beneficiary, tokenAmount);
    }

    function _ejectTokens(uint256 tokenAmount) internal {
        INoteToken(token).burn(tokenAmount);
    }

    function _processPurchase(address beneficiary, uint256 tokenAmount) internal {
        _deliverTokens(beneficiary, tokenAmount);
    }

    function _claimPayment(address payee, uint256 currencyAmount) internal {
        IERC20(currency).transferFrom(payee, address(this), currencyAmount);
    }

    function _getTokenAmount(uint256 currencyAmount) public view returns (uint256) {
        require(rate > 0, 'Crowdsale: rate is 0');
        uint256 TEN = 10;
        return
            (currencyAmount * rate * TEN**ERC20(token).decimals()) /
            (RATE_SCALING_FACTOR * TEN**ERC20(currency).decimals());
    }

    function _getCurrencyAmount(uint256 tokenAmount) internal view returns (uint256) {
        if (rate == 0) return 0;
        uint256 TEN = 10;
        return
            (tokenAmount * RATE_SCALING_FACTOR * TEN**ERC20(currency).decimals()) /
            (rate * TEN**ERC20(token).decimals());
    }

    function _forwardFunds(address beneficiary, uint256 currencyAmount) internal {
        IERC20(currency).transfer(beneficiary, currencyAmount);
    }

    function _setTotalCap(uint256 cap) internal {
        require(cap > 0, 'Crowdsale: cap is 0');
        require(cap >= currencyRaised, 'Crowdsale: cap is bellow currency raised');

        totalCap = cap;
    }

    function totalCapReached() public view returns (bool) {
        return currencyRaised >= totalCap;
    }

    function isUnderTotalCap(uint256 currencyAmount) public view returns (bool) {
        return currencyRaised + currencyAmount <= totalCap;
    }
}
","keccak256":"0x261409ca760635e5eca46a7785513ef67e0bd871f296e729a97566673111c6c5","license":"MIT"},"contracts/protocol/note-sale/crowdsale/FinalizableCrowdsale.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './TimedCrowdsale.sol';

abstract contract FinalizableCrowdsale is TimedCrowdsale {
    bool public finalized;

    event CrowdsaleFinalized();

    function finalize(bool claimRemainToken, address remainTokenRecipient)
        public
        whenNotPaused
        nonReentrant
        onlyRole(OWNER_ROLE)
    {
        require(!finalized, 'FinalizableCrowdsale: already finalized');
        require(hasClosed() || totalCapReached(), 'FinalizableCrowdsale: not closed');

        if (!isDistributedFully() && !isLongSale()) {
            uint256 tokenRemain = 0;
            tokenRemain = _getTokenAmount(getCurrencyRemainAmount());

            if (claimRemainToken) {
                _processPurchase(remainTokenRecipient, tokenRemain);
            } else {
                _ejectTokens(tokenRemain);
            }
        }

        finalized = true;

        _finalization();
        emit CrowdsaleFinalized();
    }

    function _finalization() internal virtual {
        // solhint-disable-previous-line no-empty-blocks
    }
}
","keccak256":"0xb9afe39e06535967ac174325936af5bd1b26bcf6d70de50474624be58904b663","license":"MIT"},"contracts/protocol/note-sale/crowdsale/IncreasingInterestCrowdsale.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './FinalizableCrowdsale.sol';

abstract contract IncreasingInterestCrowdsale is FinalizableCrowdsale {
    uint32 public initialInterest;
    uint32 public finalInterest;
    uint32 public timeInterval;
    uint32 public amountChangeEachInterval;

    uint32 public pickedInterest;

    function setInterestRange(
        uint32 _initialInterest,
        uint32 _finalInterest,
        uint32 _timeInterval,
        uint32 _amountChangeEachInterval
    ) public whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        require(!hasStarted, 'IncreasingInterestCrowdsale: sale already started');
        require(
            _initialInterest <= _finalInterest,
            'IncreasingInterestCrowdsale: initial interest is not less than final interest'
        );
        require(_timeInterval > 0, 'IncreasingInterestCrowdsale: time interval is 0');

        initialInterest = _initialInterest;
        finalInterest = _finalInterest;
        timeInterval = _timeInterval;
        amountChangeEachInterval = _amountChangeEachInterval;
    }

    function getCurrentInterest() public view returns (uint32) {
        if (block.timestamp < openingTime) {
            return 0;
        }

        // solhint-disable-next-line not-rely-on-time
        uint256 elapsedTime = block.timestamp - openingTime;
        uint256 numberInterval = elapsedTime / timeInterval;

        uint32 currentInterest = uint32(amountChangeEachInterval * numberInterval + initialInterest);

        if (currentInterest > finalInterest) {
            return finalInterest;
        } else {
            return currentInterest;
        }
    }

    function _finalization() internal override {
        super._finalization();

        pickedInterest = getCurrentInterest();
        ISecuritizationPool(pool).setInterestRateForSOT(pickedInterest);
    }

    function _preValidatePurchase(
        address beneficiary,
        uint256 currencyAmount,
        uint256 tokenAmount
    ) internal view override {
        super._preValidatePurchase(beneficiary, currencyAmount, tokenAmount);
        require(timeInterval > 0, 'IncreasingInterestCrowdsale: time interval not set');
    }
}
","keccak256":"0xbae6a4e893d6b7ed505627cbc32dfbff4bd772cf064b1cdcde52013ac73e3214","license":"MIT"},"contracts/protocol/note-sale/crowdsale/TimedCrowdsale.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './Crowdsale.sol';

abstract contract TimedCrowdsale is Crowdsale {
    uint256 public openingTime;
    uint256 public closingTime;

    bool public isEnableTimeLimit;

    event TimedCrowdsaleExtended(uint256 prevClosingTime, uint256 newClosingTime);

    function __TimedCrowdsale__init(
        Registry _registry,
        address _pool,
        address _token,
        address _currency
    ) internal onlyInitializing {
        __Crowdsale__init(_registry, _pool, _token, _currency);

        isEnableTimeLimit = true;
    }

    modifier onlyWhileOpen() {
        require(isOpen() || isLongSale(), 'TimedCrowdsale: not open');
        _;
    }

    function isOpen() public view returns (bool) {
        if (!isEnableTimeLimit) {
            return true;
        }
        // solhint-disable-next-line not-rely-on-time
        return block.timestamp >= openingTime && block.timestamp <= closingTime;
    }

    function hasClosed() public view returns (bool) {
        // solhint-disable-next-line not-rely-on-time
        return block.timestamp > closingTime;
    }

    function extendTime(uint256 newClosingTime) external whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        require(newClosingTime > closingTime, 'TimedCrowdsale: new closing time is before current closing time');

        emit TimedCrowdsaleExtended(closingTime, newClosingTime);
        closingTime = newClosingTime;
    }

    function newSaleRoundTime(uint256 newOpeningTime, uint256 newClosingTime) public whenNotPaused onlyRole(OWNER_ROLE) {
        require(newClosingTime >= newOpeningTime, 'TimedCrowdsale: opening time is not before closing time');
        // not accept opening time in the past
        if (newOpeningTime < block.timestamp) {
            newOpeningTime = block.timestamp;
        }

        if (newClosingTime <= newOpeningTime) {
            newClosingTime = newOpeningTime + 1;
        }

        openingTime = newOpeningTime;
        closingTime = newClosingTime;
    }

    function setUsingTimeLimit(bool usingTimeLimit) public whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        isEnableTimeLimit = usingTimeLimit;
    }
}
","keccak256":"0x38e67ce358280e5c8e4f5cd4a12f11e27270767fddf8f7b50389bfe9e6234a6f","license":"MIT"},"contracts/protocol/pool/DistributionAssessor.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import './base/Interest.sol';
import './base/SecuritizationPoolServiceBase.sol';
import '../../interfaces/INoteToken.sol';

contract DistributionAssessor is Interest, SecuritizationPoolServiceBase, IDistributionAssessor {
    using ConfigHelper for Registry;

    // get current individual asset for SOT tranche
    function getSOTTokenPrice(address pool, uint256 timestamp) public view override returns (uint256) {
        ISecuritizationPool securitizationPool = ISecuritizationPool(pool);
        ERC20 noteToken = ERC20(securitizationPool.sotToken());

        if (address(noteToken) == address(0) || noteToken.totalSupply() == 0) return 0;
        uint256 ONE_SOT_DEFAULT_PRICE = _convertTokenValueToCurrencyAmount(
            address(securitizationPool),
            address(noteToken),
            1 * 10**uint256(noteToken.decimals())
        );
        uint256 openingBlockTimestamp = securitizationPool.openingBlockTimestamp();

        if (timestamp < openingBlockTimestamp) return ONE_SOT_DEFAULT_PRICE;

        uint32 interestRateSOT = securitizationPool.interestRateSOT();

        return ONE_SOT_DEFAULT_PRICE + _calculateInterestForDuration(ONE_SOT_DEFAULT_PRICE, interestRateSOT, timestamp - openingBlockTimestamp);
    }

    // get current individual asset for SOT tranche
    function calcAssetValue(
        address pool,
        address tokenAddress,
        address investor
    ) external view override returns (uint256 principal, uint256 interest) {
        ISecuritizationPool securitizationPool = ISecuritizationPool(pool);

        address sotToken = securitizationPool.sotToken();
        address jotToken = securitizationPool.jotToken();

        require(tokenAddress == sotToken || tokenAddress == jotToken, 'DistributionAssessor: unknown-tranche-address');

        uint256 openingBlockTimestamp = securitizationPool.openingBlockTimestamp();

        if (tokenAddress == sotToken) {
            uint32 interestRateSOT = securitizationPool.interestRateSOT();
            uint256 currentPrincipal = IERC20(tokenAddress).balanceOf(investor);
            uint256 tokenRedeem = securitizationPool.lockedRedeemBalances(sotToken, investor);
            return
                _calcSeniorAssetValue(
                    currentPrincipal - tokenRedeem,
                    interestRateSOT,
                    openingBlockTimestamp,
                    block.timestamp
                );
        } else {
            return _calcPrincipalInterestJOT(pool, jotToken, investor, block.timestamp);
        }
    }

    function calcCorrespondingTotalAssetValue(
        address tokenAddress,
        address investor,
        uint256 endTime
    ) external view override returns (uint256) {
        (uint256 principal, uint256 interest) = _calcCorrespondingAssetValue(tokenAddress, investor, endTime);
        return principal + interest;
    }

    function calcCorrespondingAssetValue(
        address tokenAddress,
        address investor,
        uint256 endTime
    ) external view returns (uint256 principal, uint256 interest) {
        return _calcCorrespondingAssetValue(tokenAddress, investor, endTime);
    }

    function _calcCorrespondingAssetValue(
        address tokenAddress,
        address investor,
        uint256 endTime
    ) internal view returns (uint256 principal, uint256 interest) {
        INoteToken notesToken = INoteToken(tokenAddress);
        ISecuritizationPool securitizationPool = ISecuritizationPool(notesToken.poolAddress());

        if (Configuration.NOTE_TOKEN_TYPE(notesToken.noteTokenType()) == Configuration.NOTE_TOKEN_TYPE.SENIOR) {
            uint256 openingBlockTimestamp = securitizationPool.openingBlockTimestamp();
            uint32 interestRateSOT = securitizationPool.interestRateSOT();
            return
                _calcPrincipalInterestSOT(
                    securitizationPool,
                    tokenAddress,
                    investor,
                    interestRateSOT,
                    openingBlockTimestamp,
                    endTime
                );
        } else {
            return _calcPrincipalInterestJOT(notesToken.poolAddress(), tokenAddress, investor, endTime);
        }
    }

    function calcAssetValue(
        address pool,
        address tokenAddress,
        address[] calldata investors
    ) external view returns (uint256[] memory principals, uint256[] memory interests) {
        principals = new uint256[](investors.length);
        interests = new uint256[](investors.length);

        ISecuritizationPool securitizationPool = ISecuritizationPool(pool);

        address sotToken = securitizationPool.sotToken();
        address jotToken = securitizationPool.jotToken();
        require(tokenAddress == sotToken || tokenAddress == jotToken, 'DistributionAssessor: unknown-tranche-address');

        uint256 openingBlockTimestamp = securitizationPool.openingBlockTimestamp();

        if (tokenAddress == sotToken) {
            uint32 interestRateSOT = securitizationPool.interestRateSOT();

            for (uint256 i = 0; i < investors.length; i++) {
                (uint256 principal, uint256 interest) = _calcPrincipalInterestSOT(
                    securitizationPool,
                    sotToken,
                    investors[i],
                    interestRateSOT,
                    openingBlockTimestamp,
                    block.timestamp
                );

                principals[i] = principal;
                interests[i] = interest;
            }
        } else {
            for (uint256 i = 0; i < investors.length; i++) {
                (uint256 principal, uint256 interest) = _calcPrincipalInterestJOT(
                    pool,
                    jotToken,
                    investors[i],
                    block.timestamp
                );

                principals[i] = principal;
                interests[i] = interest;
            }
        }
    }

    function calcCorrespondingAssetValue(
        address tokenAddress,
        address[] calldata investors,
        uint256 endTime
    ) external view returns (uint256[] memory principals, uint256[] memory interests) {
        principals = new uint256[](investors.length);
        interests = new uint256[](investors.length);

        for (uint256 i = 0; i < investors.length; i++) {
            (principals[i], interests[i]) = _calcCorrespondingAssetValue(tokenAddress, investors[i], endTime);
        }
    }

    function calcTokenPrice(address pool, address tokenAddress) external view override returns (uint256) {
        ISecuritizationPool securitizationPool = ISecuritizationPool(pool);
        if (tokenAddress == securitizationPool.sotToken())
            return getSOTTokenPrice(address(securitizationPool), block.timestamp);
        else if (tokenAddress == securitizationPool.jotToken())
            return getJOTTokenPrice(securitizationPool, block.timestamp);
        return 0;
    }

    function getJOTTokenPrice(ISecuritizationPool securitizationPool, uint256 endTime) public view returns (uint256) {
        address tokenAddress = securitizationPool.jotToken();
        uint256 tokenSupply = INoteToken(tokenAddress).totalSupply();
        if (tokenAddress == address(0) || tokenSupply == 0) {
            return 0;
        }

        uint256 currencyDecimals = ERC20(securitizationPool.underlyingCurrency()).decimals();
        uint256 tokenDecimals = ERC20(tokenAddress).decimals();

        uint256 totalJotValue = _calcJuniorAssetValue(address(securitizationPool), endTime);
        uint256 totalTokenRedeem = securitizationPool.totalLockedRedeemBalances(tokenAddress);
        tokenSupply = tokenSupply - totalTokenRedeem;

        return
            currencyDecimals > tokenDecimals
                ? (totalJotValue * Configuration.PRICE_SCALING_FACTOR) /
                    (tokenSupply * 10**(currencyDecimals - tokenDecimals))
                : (totalJotValue * 10**(tokenDecimals - currencyDecimals) * Configuration.PRICE_SCALING_FACTOR) /
                    tokenSupply;
    }

    function calcSeniorAssetValue(address pool, uint256 timestamp) public view returns (address, uint256) {
        ISecuritizationPool securitizationPool = ISecuritizationPool(pool);
        INoteToken sot = INoteToken(securitizationPool.sotToken());

        uint256 price = getSOTTokenPrice(address(securitizationPool), timestamp);
        uint256 totalSotSupply = sot.totalSupply();
        uint256 ONE_SOT = 10**uint256(sot.decimals());

        return (address(sot), (price * totalSotSupply) / ONE_SOT);
    }

    function getCashBalance(address pool) public view override returns (uint256) {
        ISecuritizationPool securitizationPool = ISecuritizationPool(pool);
        return
            IERC20(securitizationPool.underlyingCurrency()).balanceOf(securitizationPool.pot()) -
            securitizationPool.totalLockedDistributeBalance();
    }

    function _calcJuniorAssetValue(address pool, uint256 timestamp) internal view returns (uint256) {
        (, uint256 seniorAssetValue) = calcSeniorAssetValue(pool, timestamp);

        uint256 available = registry.getSecuritizationPoolValueService().getExpectedAssetsValue(pool, timestamp) +
            this.getCashBalance(pool);

        // senior debt needs to be covered first
        if (available > seniorAssetValue) {
            return available - seniorAssetValue;
        }
        // currently junior would receive nothing
        return 0;
    }

    function _calcPrincipalInterestSOT(
        ISecuritizationPool securitizationPool,
        address sotToken,
        address investor,
        uint32 interestRateSOT,
        uint256 openingBlockTimestamp,
        uint256 timestamp
    ) internal view returns (uint256 principal, uint256 interest) {
        uint256 tokenRedeem = securitizationPool.lockedRedeemBalances(sotToken, investor);

        return
            _calcSeniorAssetValue(
                IERC20(sotToken).balanceOf(investor) - tokenRedeem,
                interestRateSOT,
                openingBlockTimestamp,
                timestamp
            );
    }

    function _calcPrincipalInterestJOT(
        address pool,
        address jotToken,
        address investor,
        uint256 termEndUnixTimestamp
    ) internal view returns (uint256 principal, uint256 interest) {
        uint256 tokenPrice = getJOTTokenPrice(ISecuritizationPool(pool), termEndUnixTimestamp);
        uint256 currentPrincipal = IERC20(jotToken).balanceOf(investor);
        if (tokenPrice > Configuration.PRICE_SCALING_FACTOR)
            return (
                currentPrincipal,
                (currentPrincipal * tokenPrice) / Configuration.PRICE_SCALING_FACTOR - currentPrincipal
            );
        else return ((currentPrincipal * tokenPrice) / Configuration.PRICE_SCALING_FACTOR, 0);
    }

    function _getPrincipalLeftOfSOT(ISecuritizationPool securitizationPool, address sotToken)
        internal
        view
        returns (uint256)
    {
        uint256 totalPrincipal = 0;
        uint256 totalTokenRedeem = 0;
        if (sotToken != address(0x0)) {
            totalPrincipal = IERC20(sotToken).totalSupply();
            totalTokenRedeem = securitizationPool.totalLockedRedeemBalances(sotToken);
        }

        return totalPrincipal - totalTokenRedeem;
    }

    function _calcSeniorAssetValue(
        uint256 _currentPrincipalAmount,
        uint256 _annualInterestRate,
        uint256 _startTermTimestamp,
        uint256 _timestamp
    ) internal pure returns (uint256 principal, uint256 interest) {
        principal = _currentPrincipalAmount;
        interest = chargeLendingInterest(_currentPrincipalAmount, _annualInterestRate, _startTermTimestamp, _timestamp);
    }
}
