pragma solidity 0.8.19;

import {Registry} from '../../storage/Registry.sol';
import '../../tokens/ERC721/types.sol';

abstract contract ILoanKernel {
    Registry public registry;

    /****************** */
    // CONSTANTS
    /****************** */
    enum Errors {
        // Debt has been already been issued
        DEBT_ISSUED, // 0
        // Order has already expired
        ORDER_EXPIRED, // 1
        // Debt issuance associated with order has been cancelled
        ISSUANCE_CANCELLED, // 2
        // Order has been cancelled
        ORDER_CANCELLED, // 3
        // Order parameters specify amount of creditor / debtor fees
        // that is not equivalent to the amount of underwriter / relayer fees
        ORDER_INVALID_INSUFFICIENT_OR_EXCESSIVE_FEES, // 4
        // Order parameters specify insufficient principal amount for
        // debtor to at least be able to meet his fees
        ORDER_INVALID_INSUFFICIENT_PRINCIPAL, // 5
        // Order parameters specify non zero fee for an unspecified recipient
        ORDER_INVALID_UNSPECIFIED_FEE_RECIPIENT, // 6
        // Order signatures are mismatched / malformed
        ORDER_INVALID_NON_CONSENSUAL, // 7
        // Insufficient balance or allowance for principal token transfer
        CREDITOR_BALANCE_OR_ALLOWANCE_INSUFFICIENT, // 8
        // Debt doesn't exists
        DEBT_NOT_EXISTS, // 9
        // Debtor it not completed repayment yet
        NOT_COMPLETED_REPAYMENT // 10
    }

    enum FillingAddressesIndex {
        SECURITIZATION_POOL,
        PRINCIPAL_TOKEN_ADDRESS,
        REPAYMENT_ROUTER,
        TERM_CONTRACT,
        RELAYER
    }

    enum FillingNumbersIndex {
        CREDITOR_FEE,
        ASSET_PURPOSE
    }

    bytes32 public constant NULL_ISSUANCE_HASH = bytes32(0);
    bytes16 public constant NULL_COLLATERAL_INFO_HASH = bytes16(0);
    address public constant NULL_ADDRESS = address(0x0);
    //********************************************************* */

    //****** */
    // EVENTS
    //****** */
    event LogDebtKernelError(uint8 indexed _errorId, bytes32 indexed _orderHash, string desc);

    event LogFeeTransfer(address indexed payer, address token, uint256 amount, address indexed beneficiary);

    event IssuedNewInputLoans(address[] debtor, uint256[] loanTokenIds);

    event LogDebtOrderFilled(bytes32 _agreementId, uint256 _principal, address _principalToken, address _relayer);

    //********************************************************* */

    /*********** */
    // STRUCTURES
    /*********** */

    struct LoanIssuance {
        address version;
        address termsContract;
        address[] debtors;
        bytes32[] termsContractParameters; // for different loans
        bytes32[] agreementIds;
        uint256[] salts;
    }

    struct LoanOrder {
        LoanIssuance issuance;
        address principalTokenAddress;
        uint256[] principalAmounts;
        uint256 creditorFee;
        address relayer;
        uint256[] expirationTimestampInSecs;
        bytes32[] debtOrderHashes;
        uint8[] riskScores;
        uint8 assetPurpose;
    }

    struct FillDebtOrderParam {
        address[] orderAddresses; // 0-pool, 1-principal token address, 2-repayment router, 3-term contract, 4-relayer,...
        uint256[] orderValues; //  0-creditorFee, 1-asset purpose,..., [x] principalAmounts, [x] expirationTimestampInSecs, [x] - salts, [x] - riskScores
        bytes32[] termsContractParameters; // Term contract parameters from different farmers, encoded as hash strings
        LoanAssetInfo[] latInfo;
    }

    /*********** */
    // VARIABLES
    /*********** */
    mapping(bytes32 => bool) public issuanceCancelled;
    mapping(bytes32 => bool) public debtOrderCancelled;
    mapping(bytes32 => bool) public debtOrderCompleted;

    /// @notice conclude a loan by stopping lending/loan terms or allowing the loan loss. It takes the creditor, agreement ID, and term contract as input
    function concludeLoan(address creditor, bytes32 agreementId, address termContract) public virtual;
}
","keccak256":"0x168fb80d36c3277f19d006cfdc1714f9c0363cf79338d3a7e85553396a29e3d2","license":"MIT"},"contracts/protocol/loan/ILoanRegistry.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import '../../storage/Registry.sol';

abstract contract ILoanRegistry {
    Registry public registry;

    // loan -> debtors
    struct LoanEntry {
        address loanTermContract;
        address debtor;
        address principalTokenAddress;
        bytes32 termsParam; // actually inside this param was already included P token address
        uint256 salt;
        uint256 issuanceBlockTimestamp;
        uint256 lastRepayTimestamp;
        uint256 expirationTimestamp;
        uint8 riskScore;
        Configuration.ASSET_PURPOSE assetPurpose;
    }

    event UpdateLoanEntry(bytes32 indexed tokenId, LoanEntry entry);
    event UpdateCompleteLoan(bytes32 indexed tokenId, bool status);

    mapping(bytes32 => LoanEntry) public entries;

    mapping(bytes32 => bool) public manualInterestLoan;
    mapping(bytes32 => uint256) public manualInterestAmountLoan;

    mapping(bytes32 => bool) public completedLoans;

    function initialize(Registry _registry) public virtual;

    /**
     * Record new External Loan to blockchain
     */
    function insert(
        bytes32 tokenId,
        address termContract,
        address debtor,
        bytes32 termsContractParameter,
        address pTokenAddress,
        uint256 _salt,
        uint256 expirationTimestampInSecs,
        uint8[] calldata assetPurposeAndRiskScore
    ) external virtual returns (bool);

    /// @notice retrieves loan information
    function getEntry(bytes32 agreementId) public view virtual returns (LoanEntry memory);

    /// @notice retrieves the debtor's address for a given loan
    function getLoanDebtor(bytes32 tokenId) public view virtual returns (address);

    /// @notice retrieves the term contract parameters for a given loan
    function getLoanTermParams(bytes32 tokenId) public view virtual returns (bytes32);

    /// @notice retrieves the principal token address for a given loan agreement ID
    function getPrincipalTokenAddress(bytes32 agreementId) public view virtual returns (address);

    /// @notice retrieves the debtor's address for a given loan agreement ID
    function getDebtor(bytes32 agreementId) public view virtual returns (address);

    /// @notice retrieves the term contract address for a given loan agreement ID
    function getTermContract(bytes32 agreementId) public view virtual returns (address);

    /// @notice retrieves the risk score for a given loan agreement ID
    function getRiskScore(bytes32 agreementId) public view virtual returns (uint8);

    /// @notice retrieves the asset purpose for a given loan agreement ID
    function getAssetPurpose(bytes32 agreementId) public view virtual returns (Configuration.ASSET_PURPOSE);

    /// @notice retrieves the timestamp of the block at which a debt agreement was issued
    function getIssuanceBlockTimestamp(bytes32 agreementId) public view virtual returns (uint256 timestamp);

    /// @notice retrieves the timestamp of the last repayment made for a given loan agreement ID
    function getLastRepaymentTimestamp(bytes32 agreementId) public view virtual returns (uint256 timestamp);

    /// @notice retrieves the terms contract parameters for a given loan agreement ID
    function getTermsContractParameters(bytes32 agreementId) public view virtual returns (bytes32);

    /// @notice retrieves the expiration timestamp for a given loan agreement ID
    function getExpirationTimestamp(bytes32 agreementId) public view virtual returns (uint256);

    /// @notice updates the timestamp of the last repayment made for a given loan agreement ID
    function updateLastRepaymentTimestamp(bytes32 agreementId, uint256 newTimestamp) public virtual;

    /// @notice retrieves information about the principal payment for a given loan agreement ID
    /// @dev Get principal payment info before start doing repayment
    function principalPaymentInfo(
        bytes32 agreementId
    ) public view virtual returns (address pTokenAddress, uint256 pAmount);

    /// @notice marks a loan agreement as completed by setting the completedLoans mapping entry to true for a given agreement ID
    function setCompletedLoan(bytes32 agreementId) public virtual;
}
","keccak256":"0xd4bb1de57d029d3fe4a75813684b469b82674615e446913e13d5c5144d7d0537","license":"MIT"},"contracts/protocol/loan/ILoanRepaymentRouter.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import {UntangledBase} from '../../base/UntangledBase.sol';
import {Registry} from '../../storage/Registry.sol';

abstract contract ILoanRepaymentRouter is UntangledBase {
    Registry public registry;

    enum Errors {
        DEBT_AGREEMENT_NONEXISTENT,
        PAYER_BALANCE_OR_ALLOWANCE_INSUFFICIENT,
        REPAYMENT_REJECTED_BY_TERMS_CONTRACT
    }

    event LogOutputSubmit(bytes32 indexed _agreementId, uint256 indexed _tokenIndex, uint256 _totalAmount);

    function initialize(Registry _registry) public virtual;

    event LogRepayment(
        bytes32 indexed _agreementId,
        address indexed _payer,
        address indexed _beneficiary,
        uint256 _amount,
        address _token
    );

    event LogRepayments(bytes32[] _agreementIds, address _payer, uint256[] _amounts);

    event LogError(uint8 indexed _errorId, bytes32 indexed _agreementId);

    /// @notice allows batch repayment of multiple loans by iterating over the given agreement IDs and amounts
    /// @dev calls _assertRepaymentRequest and _doRepay for each repayment, and emits the LogRepayments event to indicate the successful batch repayment
    function repayInBatch(
        bytes32[] calldata agreementIds,
        uint256[] calldata amounts,
        address tokenAddress
    ) external virtual returns (bool);

    uint256[49] private __gap;
}
