contracts/interfaces/ILoanInterestTermsC.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '../storage/Registry.sol';

abstract contract ILoanInterestTermsContract {
    Registry public registry;

    mapping(bytes32 => bool) public startedLoan;

    mapping(bytes32 => uint256) public repaidPrincipalAmounts;
    mapping(bytes32 => uint256) public repaidInterestAmounts;

    mapping(bytes32 => bool) public completedRepayment;

    // Register to start Loan term for batch of agreement Ids
    function registerTermStart(bytes32 agreementId) public virtual returns (bool);

    function getValueRepaidToDate(bytes32 agreementId) public view virtual returns (uint256, uint256);

    function isCompletedRepayments(bytes32[] memory agreementIds) public view virtual returns (bool[] memory);

    /**
     * Expected repayment value with Amortization of Interest and Principal
     * (AMORTIZATION) - will be used for repayment from Debtor
     */
    function getExpectedRepaymentValues(bytes32 agreementId, uint256 timestamp)
        public
        view
        virtual
        returns (uint256 expectedPrincipal, uint256 expectedInterest);

    function getMultiExpectedRepaymentValues(bytes32[] memory agreementIds, uint256 timestamp)
        public
        view
        virtual
        returns (uint256[] memory, uint256[] memory);

    function registerConcludeLoan(bytes32 agreementId) external virtual returns (bool);

    /// When called, the registerRepayment function records the debtor's
    ///  repayment, as well as any auxiliary metadata needed by the contract
    ///  to determine ex post facto the value repaid (e.g. current USD
    ///  exchange rate)
    /// @param  agreementId bytes32. The agreement id (issuance hash) of the debt agreement to which this pertains.
    /// @param  payer address. The address of the payer.
    /// @param  beneficiary address. The address of the payment's beneficiary.
    /// @param  unitsOfRepayment uint. The units-of-value repaid in the transaction.
    /// @param  tokenAddress address. The address of the token with which the repayment transaction was executed.
    function registerRepayment(
        bytes32 agreementId,
        address payer,
        address beneficiary,
        uint256 unitsOfRepayment,
        address tokenAddress
    ) public virtual returns (uint256 remains);

    function getInterestRate(bytes32 agreementId) public view virtual returns (uint256);
}

contracts/interfaces/ILoanKernel.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '../storage/Registry.sol';

abstract contract ILoanKernel {
    Registry public registry;

    /****************** */
    // CONSTANTS
    /****************** */
    enum Errors {
        // Debt has been already been issued
        DEBT_ISSUED, // 0
        // Order has already expired
        ORDER_EXPIRED, // 1
        // Debt issuance associated with order has been cancelled
        ISSUANCE_CANCELLED, // 2
        // Order has been cancelled
        ORDER_CANCELLED, // 3
        // Order parameters specify amount of creditor / debtor fees
        // that is not equivalent to the amount of underwriter / relayer fees
        ORDER_INVALID_INSUFFICIENT_OR_EXCESSIVE_FEES, // 4
        // Order parameters specify insufficient principal amount for
        // debtor to at least be able to meet his fees
        ORDER_INVALID_INSUFFICIENT_PRINCIPAL, // 5
        // Order parameters specify non zero fee for an unspecified recipient
        ORDER_INVALID_UNSPECIFIED_FEE_RECIPIENT, // 6
        // Order signatures are mismatched / malformed
        ORDER_INVALID_NON_CONSENSUAL, // 7
        // Insufficient balance or allowance for principal token transfer
        CREDITOR_BALANCE_OR_ALLOWANCE_INSUFFICIENT, // 8
        // Debt doesn't exists
        DEBT_NOT_EXISTS, // 9
        // Debtor it not completed repayment yet
        NOT_COMPLETED_REPAYMENT // 10
    }

    enum FillingAddressesIndex {
        CREDITOR,
        PRINCIPAL_TOKEN_ADDRESS,
        REPAYMENT_ROUTER,
        TERM_CONTRACT,
        RELAYER
    }

    enum FillingNumbersIndex {
        CREDITOR_FEE,
        ASSET_PURPOSE
    }

    bytes32 public constant NULL_ISSUANCE_HASH = bytes32(0);
    bytes16 public constant NULL_COLLATERAL_INFO_HASH = bytes16(0);
    address public constant NULL_ADDRESS = address(0x0);
    //********************************************************* */

    //****** */
    // EVENTS
    //****** */
    event LogDebtKernelError(uint8 indexed _errorId, bytes32 indexed _orderHash, string desc);

    event LogFeeTransfer(address indexed payer, address token, uint256 amount, address indexed beneficiary);
    event LogDebtOrderFilled(
        bytes32 agreementId,
        uint256[] principalAmounts,
        address principalTokenAddress,
        address relayer
    );

    event IssuedNewInputLoans(address[] debtor, uint256[] loanTokenIds);

    event LogDebtOrderFilled(bytes32 _agreementId, uint256 _principal, address _principalToken, address _relayer);

    //********************************************************* */

    /*********** */
    // STRUCTURES
    /*********** */

    struct LoanIssuance {
        address version;
        address termsContract;
        address[] debtors;
        bytes32[] termsContractParameters; // for different loans
        bytes32[] agreementIds;
        uint256[] salts;
    }

    struct LoanOrder {
        LoanIssuance issuance;
        address principalTokenAddress;
        uint256[] principalAmounts;
        uint256 creditorFee;
        address relayer;
        uint256[] expirationTimestampInSecs;
        bytes32[] debtOrderHashes;
        uint8[] riskScores;
        uint8 assetPurpose;
    }

    /*********** */
    // VARIABLES
    /*********** */
    mapping(bytes32 => bool) public issuanceCancelled;
    mapping(bytes32 => bool) public debtOrderCancelled;
    mapping(bytes32 => bool) public debtOrderCompleted;

    function concludeLoan(
        address creditor,
        bytes32 agreementId,
        address termContract
    ) public virtual;
}

