pragma solidity 0.8.19;

import './Crowdsale.sol';

/// @title TimedCrowdsale
/// @author Untangled Team
/// @dev Note sale that has open time and close time
abstract contract TimedCrowdsale is Crowdsale {
    using ConfigHelper for Registry;
    uint256 public openingTime;
    uint256 public closingTime;

    bool public isEnableTimeLimit;

    event TimedCrowdsaleExtended(uint256 prevClosingTime, uint256 newClosingTime);
    event UpdateUsingTimeLimit(bool isEnableTimeLimit);
    event UpdateSaleRoundTime(uint256 newOpeningTime, uint256 newClosingTime);

    function __TimedCrowdsale__init(
        Registry _registry,
        address _pool,
        address _token,
        address _currency
    ) internal onlyInitializing {
        __Crowdsale__init(_registry, _pool, _token, _currency);

        isEnableTimeLimit = true;
    }

    modifier onlyWhileOpen() {
        require(isOpen() || isLongSale(), 'TimedCrowdsale: not open');
        _;
    }

    function isOpen() public view returns (bool) {
        if (!isEnableTimeLimit) {
            return true;
        }
        // solhint-disable-next-line not-rely-on-time
        return block.timestamp >= openingTime && block.timestamp <= closingTime;
    }

    function hasClosed() public view returns (bool) {
        // solhint-disable-next-line not-rely-on-time
        return block.timestamp > closingTime;
    }

    /// @notice Updates the closing time with the new closing time
    function extendTime(uint256 newClosingTime) external whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        require(newClosingTime > closingTime, 'TimedCrowdsale: new closing time is before current closing time');

        emit TimedCrowdsaleExtended(closingTime, newClosingTime);
        closingTime = newClosingTime;
    }

    /// @notice Updates the opening time and closing time accordingly
    function newSaleRoundTime(uint256 newOpeningTime, uint256 newClosingTime) public whenNotPaused {
        require(hasRole(OWNER_ROLE, _msgSender()) || _msgSender() == address(registry.getSecuritizationManager()), "Crowdsale: Caller must be owner or pool");
        require(newClosingTime >= newOpeningTime, 'TimedCrowdsale: opening time is not before closing time');
        // not accept opening time in the past
        if (newOpeningTime < block.timestamp) {
            newOpeningTime = block.timestamp;
        }

        if (newClosingTime <= newOpeningTime) {
            newClosingTime = newOpeningTime + 1;
        }

        openingTime = newOpeningTime;
        closingTime = newClosingTime;

        emit UpdateSaleRoundTime(newOpeningTime, newClosingTime);
    }

    /// @dev Sets the isEnableTimeLimit variable to the specified value
    function setUsingTimeLimit(bool usingTimeLimit) public whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        isEnableTimeLimit = usingTimeLimit;
        emit UpdateUsingTimeLimit(usingTimeLimit);
    }

    uint256[47] private __gap;
}
","keccak256":"0x54de875cc0b278a38d3921666164b1809875dd1cf7075d118a6ad4e317152e81","license":"MIT"},"contracts/protocol/note-sale/fab/TokenGenerationEventFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import '../../../base/UntangledBase.sol';
import '../../../interfaces/ITokenGenerationEventFactory.sol';
import '../../../libraries/ConfigHelper.sol';
import '../../../base/Factory.sol';
import '../../../libraries/UntangledMath.sol';

contract TokenGenerationEventFactory is ITokenGenerationEventFactory, UntangledBase, Factory {
    using ConfigHelper for Registry;

    enum SaleType {
        MINTED_INCREASING_INTEREST_SOT,
        NORMAL_SALE_JOT,
        NORMAL_SALE_SOT
    }

    function initialize(Registry _registry, address _factoryAdmin) public initializer {
        __UntangledBase__init(_msgSender());
        __Factory__init(_factoryAdmin);

        registry = _registry;
    }

    modifier onlySecuritizationManager() {
        require(
            _msgSender() == address(registry.getSecuritizationManager()),
            'SecuritizationPool: Only SecuritizationManager'
        );
        _;
    }

    function setFactoryAdmin(address _factoryAdmin) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _setFactoryAdmin(_factoryAdmin);
    }

    function createNewSaleInstance(
        address issuerTokenController,
        address pool,
        address token,
        address currency,
        uint8 saleType,
        bool longSale
    ) external override nonReentrant onlySecuritizationManager returns (address) {
        if (saleType == uint8(SaleType.MINTED_INCREASING_INTEREST_SOT)) {
            return _newMintedIncreasingInterestSale(issuerTokenController, pool, token, currency, longSale);
        } else if (saleType == uint8(SaleType.NORMAL_SALE_JOT)) {
            return _newNormalSale(issuerTokenController, pool, token, currency, longSale);
        } else if (saleType == uint8(SaleType.NORMAL_SALE_SOT)) {
            return _newNormalSale(issuerTokenController, pool, token, currency, longSale);
        } else {
            revert('Unknown sale type');
        }
    }

    function _newMintedIncreasingInterestSale(
        address issuerTokenController,
        address pool,
        address token,
        address currency,
        bool longSale
    ) private returns (address) {
        address mintedIncreasingInterestTGEImplAddress = address(registry.getMintedIncreasingInterestTGE());

        bytes memory _initialData = abi.encodeWithSelector(
            getSelector('initialize(address,address,address,address,bool)'),
            registry,
            pool,
            token,
            currency,
            longSale
        );

        address tgeAddress = _deployInstance(mintedIncreasingInterestTGEImplAddress, _initialData);
        MintedIncreasingInterestTGE tge = MintedIncreasingInterestTGE(tgeAddress);

        tge.grantRole(tge.OWNER_ROLE(), issuerTokenController);
        tge.renounceRole(tge.OWNER_ROLE(), address(this));

        tgeAddresses.push(tgeAddress);
        isExistingTge[tgeAddress] = true;

        emit TokenGenerationEventCreated(tgeAddress);

        return tgeAddress;
    }

    function _newNormalSale(
        address issuerTokenController,
        address pool,
        address token,
        address currency,
        bool longSale
    ) private returns (address) {
        address mintedNormalTGEImplAddress = address(registry.getMintedNormalTGE());

        bytes memory _initialData = abi.encodeWithSelector(
            getSelector('initialize(address,address,address,address,bool)'),
            registry,
            pool,
            token,
            currency,
            longSale
        );

        address tgeAddress = _deployInstance(mintedNormalTGEImplAddress, _initialData);
        MintedNormalTGE tge = MintedNormalTGE(tgeAddress);

        tge.grantRole(tge.OWNER_ROLE(), issuerTokenController);
        tge.renounceRole(tge.OWNER_ROLE(), address(this));

        tgeAddresses.push(tgeAddress);
        isExistingTge[tgeAddress] = true;

        emit TokenGenerationEventCreated(tgeAddress);

        return tgeAddress;
    }

    function pauseUnpauseTge(address tgeAdress) external whenNotPaused nonReentrant onlyRole(DEFAULT_ADMIN_ROLE) {
        require(isExistingTge[tgeAdress], 'TokenGenerationEventFactory: tge does not exist');
        MintedIncreasingInterestTGE tge = MintedIncreasingInterestTGE(tgeAdress);
        if (tge.paused()) {
            tge.unpause();
        } else {
            tge.pause();
        }
    }

    function pauseUnpauseAllTges() external whenNotPaused nonReentrant onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 tgeAddressesLength = tgeAddresses.length;
        for (uint256 i = 0; i < tgeAddressesLength; i = UntangledMath.uncheckedInc(i)) {
            MintedIncreasingInterestTGE tge = MintedIncreasingInterestTGE(tgeAddresses[i]);
            if (tge.paused()) {
                tge.unpause();
            } else {
                tge.pause();
            }
        }
    }

    uint256[50] private __gap;
}
